import React, { useState } from "react";
import {
  Box,
  Button,
  FormControl,
  FormHelperText,
  Grid,
  TextField,
  Typography,
  Container,
} from '@mui/material/';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailError, setEmailError] = useState(false);
  const [passwordError, setPasswordError] = useState(false);
  const [validForm, setValidForm] = useState(false);
  const [showError, setShowError] = useState(false);

  const validateForm = () => {
    const validEmail = /\S+@\S+\.\S+/.test(email);
    const validPassword = password !== '';
    setEmailError(!validEmail);
    setPasswordError(!validPassword);
    setValidForm(validEmail && validPassword);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Burada giriş işlemi yapılabilir
    // Eğer kullanıcı bilgileri doğruysa, kütüphane sayfasına yönlendirilebilir
    // Eğer kullanıcı bilgileri yanlışsa, hata mesajı gösterilebilir
    setShowError(true);
  };

  return (
    <Container component="main" maxWidth="xs" p={2}>
      <Typography variant="h4">Login</Typography>
      <form onSubmit={handleSubmit}>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <FormControl fullWidth error={emailError}>
              <TextField
                label="Email"
                value={email}
                onChange={(event) => setEmail(event.target.value)}
                onBlur={validateForm}
                required
              />
              {emailError && (
                <FormHelperText>Please enter a valid email address</FormHelperText>
              )}
            </FormControl>
          </Grid>
          <Grid item xs={12}>
            <FormControl fullWidth error={passwordError}>
              <TextField
                label="Password"
                type="password"
                value={password}
                onChange={(event) => setPassword(event.target.value)}
                onBlur={validateForm}
                required
              />
              {passwordError && <FormHelperText>Please enter a password</FormHelperText>}
            </FormControl>
          </Grid>
          <Grid item xs={12}>
            <Button
              type="submit"
              variant="contained"
              color="primary"
              disabled={!validForm}
            >
              Login
            </Button>
            {showError && (
              <Typography color="error">Incorrect email or password</Typography>
            )}
          </Grid>
        </Grid>
      </form>
      <Box mt={3}>
        <Typography variant="body2">
          Don't have an account? <a href="#">Sign up</a>
        </Typography>
      </Box>
    </Container>
  );
};

export default Login;
