        <Container component="main" maxWidth="xs" pt={2}>
            <Typography variant="h4">Login</Typography>
            <form onSubmit={handleSubmit}>
                <Grid container spacing={3}>
                    <Grid item xs={12}>
                        <FormControl fullWidth error={emailError}>
                            <TextField
                                label="Email"
                                value={email}
                                onChange={(event) => setEmail(event.target.value)}
                                onBlur={validateForm}
                                required
                            />
                            {emailError && (
                                <FormHelperText>Please enter a valid email address</FormHelperText>
                            )}
                        </FormControl>
                    </Grid>
                    <Grid item xs={12}>
                        <FormControl fullWidth error={passwordError}>
                            <TextField
                                label="Password"
                                type="password"
                                value={password}
                                onChange={(event) => setPassword(event.target.value)}
                                onBlur={validateForm}
                                required
                            />
                            {passwordError && <FormHelperText>Please enter a password</FormHelperText>}
                        </FormControl>
                    </Grid>
                    <Grid item xs={12}>
                        <Button
                            type="submit"
                            variant="contained"
                            color="primary"
                            disabled={!validForm}
                        >
                            Login
                        </Button>
                    </Grid>
                </Grid>
            </form>
        </Container>
    )
}

export default Login;
