import React, { useState } from "react";
import {
    Box,
    Button,
    FormControl,
    FormHelperText,
    Grid,
    TextField,
    Typography,
    Container,
} from "@mui/material/";

const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [emailError, setEmailError] = useState(false);
    const [passwordError, setPasswordError] = useState(false);
    const [validForm, setValidForm] = useState(false);
    const [showError, setShowError] = useState(false);

    const validateEmail = (email) => {
        // Email alanının uygun formatta olup olmadığını kontrol eden fonksiyon
        const re = /\S+@\S+\.\S+/;
        return re.test(String(email).toLowerCase());
    };

    const validateForm = () => {
        // Form alanlarını kontrol eden fonksiyon
        const validEmail = validateEmail(email);
        const validPassword = password.trim() !== "";
        setEmailError(!validEmail);
        setPasswordError(!validPassword);
        setValidForm(validEmail && validPassword);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // Burada giriş işlemi yapılabilir
        // Eğer kullanıcı bilgileri doğruysa, kütüphane sayfasına yönlendirilebilir
        // Eğer kullanıcı bilgileri yanlışsa, hata mesajı gösterilebilir
        setShowError(true);
    };

    return (
        <Container component="main" maxWidth="xs" pt={2}>
            <Typography variant="h4">Login</Typography>
            <form onSubmit={handleSubmit}>
                <Grid container spacing={3}>
                    <Grid item xs={12}>
                        <FormControl fullWidth error={emailError}>
                            <TextField
                                label="Email"
                                value={email}
                                onChange={(event) => setEmail(event.target.value)}
                                onBlur={validateForm}
                                required
                            />
                            {emailError && (
                                <FormHelperText>Please enter a valid email address</FormHelperText>
                            )}
                        </FormControl>
                    </Grid>
                    <Grid item xs={12}>
                        <FormControl fullWidth error={passwordError}>
                            <TextField
                                label="Password"
                                type="password"
                                value={password}
                                onChange={(event) => setPassword(event.target.value)}
                                onBlur={validateForm}
                                required
                            />
                            {passwordError && <FormHelperText>Please enter a password</FormHelperText>}
                        </FormControl>
                    </Grid>
                    <Grid item xs={12}>
                        <Button
                            type="submit"
                            variant="contained"
                            color="primary"
                            disabled={!validForm}
                        >
                            Login
                        </Button>
                    </Grid>
                </Grid>
            </form>
        </Container>
    )
}

export default Login;
