import React, { useState } from "react";
import {
  Box,
  Button,
  FormControl,
  FormHelperText,
  Grid,
  TextField,
  Typography,
  Container,
} from '@mui/material/';
import { useHistory } from 'react-router-dom';

const Register = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [passwordConfirm, setPasswordConfirm] = useState('');
  const [emailError, setEmailError] = useState(false);
  const [passwordError, setPasswordError] = useState(false);
  const [passwordConfirmError, setPasswordConfirmError] = useState(false);
  const [validForm, setValidForm] = useState(false);
  const history = useHistory();

  const validateForm = () => {
    const validEmail = /\S+@\S+\.\S+/.test(email);
    const validPassword = password !== '';
    const validPasswordConfirm = passwordConfirm !== '' && password === passwordConfirm;
    setEmailError(!validEmail);
    setPasswordError(!validPassword);
    setPasswordConfirmError(!validPasswordConfirm);
    setValidForm(validEmail && validPassword && validPasswordConfirm);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Burada kayıt işlemi yapılabilir
    // Eğer kayıt başarılıysa, giriş sayfasına yönlendirilebilir
    history.push('/login');
  };

  return (
    <Container component="main" maxWidth="xs" pt={2}>
      <Typography variant="h4" my={2}>Register</Typography>
      <form onSubmit={handleSubmit}>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <FormControl fullWidth error={emailError}>
              <TextField
                label="Email"
                value={email}
                onChange={(event) => setEmail(event.target.value)}
                onBlur={validateForm}
                required
              />
              {emailError && (
                <FormHelperText>Please enter a valid email address</FormHelperText>
              )}
            </FormControl>
          </Grid>
          <Grid item xs={12}>
            <FormControl fullWidth error={passwordError}>
              <TextField
                label="Password"
                type="password"
                value={password}
                onChange={(event) => setPassword(event.target.value)}
                onBlur={validateForm}
                required
              />
              {passwordError && <FormHelperText>Please enter a password</FormHelperText>}
            </FormControl>
          </Grid>
          <Grid item xs={12}>
            <FormControl fullWidth error={passwordConfirmError}>
              <TextField
                label="Confirm Password"
                type="password"
                value={passwordConfirm}
                onChange={(event) => setPasswordConfirm(event.target.value)}
                onBlur={validateForm}
                required
              />
              {passwordConfirmError && (
                <FormHelperText>Please enter a matching password</FormHelperText>
              )}
            </FormControl>
            <Button
            fullWidth
            size="large"
            sx={{ my: 2 }}
              type="submit"
              variant="contained"
              color="primary"
              disabled={!validForm}
            >Register</Button>
            <Button fullWidth onClick={() => history.push('/login')}>Cancel</Button>
            </Grid>
        </Grid>
      </form>
    </Container>
  );
};
export default Register;
